import { makeStyles } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";

import {useState, useEffect} from "react";

import MenuOptions from './MenuOptions.js';

function MenuDrawer() {

    const useStyles = makeStyles((theme) => ({
        menuButton: {
            marginRight: theme.spacing(2),
        },
        list: {
            width: 250,
        }
    }));

    const styles = useStyles();
    const [isVisible, setIsVisible] = useState(true);
    let change = true;
    let count = 0;

    useEffect(() => {
        setIsVisible(change)
    }, [change])

    function toggleDrawer(vis) {
        console.log('should be ' + vis);
        change = vis;
        count = count + 1;
    }

    return (
        <div>
            <IconButton edge='start' className={styles.menuButton} color='inherit' aria-label='menu' onClick={toggleDrawer(true)}>
                <MenuIcon />
            </IconButton>
            <Drawer anchor='left' open={isVisible} onClose={toggleDrawer(false)}>
                <MenuOptions />
            </Drawer>
        </div>
    );
}

export default MenuDrawer;
