import moment from 'moment';

const currentDate = moment();
let date = currentDate.date();

let classes = [ {
    title: 'Pottery',
    startDate: new Date(2021, 6, 7, 8, 0),
    endDate: new Date(2021, 6, 7, 10, 0),
    id: 0,
    location: 'Room 1',
}, {
    title: 'Pottery',
    startDate: new Date(2021, 6, 8, 9, 0),
    endDate: new Date(2021, 6, 8, 11, 0),
    id: 0,
    location: 'Room 1',
}, {
    title: 'Swim',
    startDate: new Date(2021, 6, 8, 9, 0),
    endDate: new Date(2021, 6, 8, 10, 0),
    id: 0,
    location: 'Pool',
}, {
    title: 'Nonna\'s Cooking Class',
    startDate: new Date(2021, 6, 10, 11, 0),
    endDate: new Date(2021, 6, 10, 12, 0),
    id: 0,
    location: 'Room 2',
}, {
    title: 'Karate',
    startDate: new Date(2021, 6, 11, 8, 0),
    endDate: new Date(2021, 6, 11, 10, 0),
    id: 0,
    location: 'Gym',
}]

const makeTodayClass = (startDate, endDate) => {
    const days = moment(startDate).diff(endDate, 'days');
    const nextStartDate = moment(startDate)
        .year(currentDate.year())
        .month(currentDate.month())
        .date(date);
    const nextEndDate = moment(endDate)
        .year(currentDate.year())
        .month(currentDate.month())
        .date(date + days);

    return {
        startDate: nextStartDate.toDate(),
        endDate: nextEndDate.toDate(),
    };
};

export default classes.map(({ startDate, endDate, ...restArgs }) => {
    const result = {
        ...makeTodayClass(startDate, endDate),
        ...restArgs,
    };
    date += 1;
    if (date > 31) date = 1;
    return result;
});
